export default (state, action) => {
    switch(action.type) {
        case 'DELETE_PROJECT':
            return {
                ...state,
                projects: state.projects.filter(project => project.id !== action.payload)
            }
        case 'ADD_PROJECT':
            return {
                ...state,
                projects: [action.payload, ...state.projects]
            }
        default:
            return state;
    }
}
