import React, {useState, useContext} from 'react';
import { GlobalContext } from '../../context/GlobalState.js';

import './feedbackStyle.css';

export const AddFeedback = () => {
    const [text, setText] = useState('');
    const [subject, setSubject] = useState('');


    const { addComment, negateSeenComment } = useContext(GlobalContext);

    const onSubmit = e => {
        e.preventDefault();

        const newComment = {
            id: Math.floor(Math.random() * 100000000),
            text,
            subject
        }

        addComment(newComment);
        negateSeenComment();
    }

    return (
        <div className="formStyle">
            <h1>Honest Feedback</h1>
            <form onSubmit={onSubmit}>
                {/* Subject line */}
                <label className="labelStyle">Subject</label> <br/><br/>
                <input 
                    type="text" 
                    id="subject"
                    className="subjectStyle"
                    value={subject} 
                    onChange={(e) => setSubject(e.target.value)} 
                    placeholder="Enter text..." /> <br/><br/>

                {/* Feedback section */}
                <label className="labelStyle">Feedback</label><br/><br/>
                <textarea 
                    id="text"
                    className="feedbackStyle"
                    value={text} 
                    onChange={(e) => setText(e.target.value)} 
                    placeholder="Enter text..." /> <br/><br/><br/>

                {/* Submit Button */}
                <button className="submitStyle">Submit</button>
            </form>
        </div>
    )
}
